mapper.select:
-- String mclass, String area, String lang, String palyfrom, String level, String hide, String key
SELECT *,FROM_UNIXTIME(m_addtime,'%Y-%m-%d %H:%i:%S') as addtime FROM movie JOIN movie_class on m_class=c_sort



    m_class=#{mclass}


    and m_area=#{area}


    and m_lang=#{lang}


    and m_playfrom=#{playfrom}


    and m_level=#{level}


    and m_hide=#{hide}


    and m_name='%'+#{key}+'%'



order by m_addtime desc
limit 15

